import Header from "./components/Layout/Header";
import React, { useState, Fragment } from "react";

function App() {
  const dummyData = [
    {
      id: 1,
      percent: 80,
    },
    {
      id: 2,
      percent: 15,
    },
    {
      id: 3,
      percent: 5,
    },
  ];

  const getRandomNumber = () => {
    let sortPrizes = dummyData.sort((a, b) => a.percent - b.percent);
    console.log(sortPrizes);
    let weightArray = sortPrizes.map((_, index) => {
      //利用 .map 做一個計數累加
      let count = 0;
      for (let i = 0; i <= index; i++) {
        count += sortPrizes[i].percent;
      }
      return count;
    });
    console.log("weightArray: ", weightArray);
    let index;
    for (let i = 0; i <= weightArray.length; i++) {
      if (weightArray[i] >= random) {
        index = i;
        break;
      }
    }
    console.log("random: ", random);
    console.log("index: ", index);
    return sortPrizes[index].id;
  };

  getRandomNumber();

  return (
    <React.Fragment>
      <Header />
      <button>Click Me!</button>
    </React.Fragment>
  );
}

export default App;
